import { Component, OnInit } from '@angular/core';
import {
  FormControl,
  UntypedFormArray,
  UntypedFormGroup,
} from '@angular/forms';
import { MonsterControllerService } from '@monster/enter-monster/services/monster-controller/monster-controller.service';
import { WeaponEquivalence, WwwMonster } from '@shared/model/www-monster.model';
import { buildFormFromObject } from '@shared/utilities/form-util/form.util';

/** Presenter element for user entry of new monsters. UNDER CONSTRUCTION */
@Component({
  selector: 'greg-monster-form',
  templateUrl: './monster-form.component.html',
  styleUrls: ['./monster-form.component.scss'],
})
export class MonsterFormComponent implements OnInit {
  monsterForm: UntypedFormGroup;

  systems: Object;
  get systemsList(): string[] {
    return Object.keys(this.systems);
  }

  get meleeAttacks(): UntypedFormArray {
    return this.monsterForm.get('meleeAttacks') as UntypedFormArray;
  }

  get missileAttacks(): UntypedFormArray {
    return this.monsterForm.get('missileAttacks') as UntypedFormArray;
  }

  /** MonsterFormComponent Constructor */
  constructor(private monsterController: MonsterControllerService) {}

  /** Initializer Method */
  ngOnInit(): void {
    this.initializeMonsterForm();
    this.initializeSystemsMap();
  }

  addMeleeAttack(): void {
    this.meleeAttacks.push(buildFormFromObject(new WeaponEquivalence()));
  }

  addMissileAttack(): void {
    this.missileAttacks.push(buildFormFromObject(new WeaponEquivalence()));
  }

  addMonsterToSource(): void {
    this.monsterController.addMonster(this.monsterForm.value);
    this.initializeMonsterForm();
  }

  private initializeMonsterForm(): void {
    this.monsterForm = buildFormFromObject(
      new WwwMonster()
    ) as UntypedFormGroup;
    this.monsterForm.addControl('system', new FormControl<string>('www'));
  }

  private initializeSystemsMap(): void {
    this.systems = {};
    this.systems['bx'] = 'Basic / Expert';
    this.systems['www'] = 'Weapons, Wits, & Wizardry';
  }
}
<form [formGroup]="monsterForm">
  <label>System:</label>
  <select formControlName="system">
    <option *ngFor="let systemCode of systemsList" [ngValue]="systemCode">
      {{ systems[systemCode] }}
    </option>
  </select>

  <label>Name</label>
  <input formControlName="name" type="string" />

  <label>Movement:</label>
  <span>
    <input formControlName="movement" type="number" />" (<input
      formControlName="charge"
      type="number"
    />")
  </span>

  <label>No. Appearing:</label>
  <greg-dice-roller-form
    name="noAppearing"
    [parentForm]="monsterForm.get('noAppearing')"
  ></greg-dice-roller-form>

  <label>Chance In Lair:</label>
  <span> <input formControlName="chanceInLair" type="number" />% </span>

  <label>Treasure Type:</label>
  <input formControlName="treasureType" type="string" />

  <label>Morale:</label>
  <input formControlName="morale" type="number" />

  <span>
    <label>Melee Attacks:</label>
    <br />
    <button (click)="addMeleeAttack()">Add Attack</button>
  </span>
  <div formArrayName="meleeAttacks">
    <table>
      <tr>
        <th>Man Equiv.</th>
        <th>Attack Type</th>
        <th>Modifier</th>
        <th>Length</th>
      </tr>
      <tr *ngFor="let melee of meleeAttacks.controls; index as i">
        <ng-container [formGroupName]="i">
          <td>
            <input formControlName="no" type="number" />
          </td>
          <td>
            <input formControlName="type" type="string" />
          </td>
          <td>
            <input formControlName="modifier" type="number" />
          </td>
          <td>
            <input formControlName="length" type="number" />
          </td>
        </ng-container>
      </tr>
    </table>
  </div>

  <span>
    <label>Missile Attacks:</label>
    <br />
    <button (click)="addMissileAttack()">Add Missile Attack</button>
  </span>
  <div formArrayName="missileAttacks">
    <table>
      <tr>
        <th>Man Equiv.</th>
        <th>Attack Type</th>
        <th>Modifier</th>
      </tr>
      <tr *ngFor="let attack of missileAttacks.controls; index as i">
        <ng-container [formGroupName]="i">
          <td><input formControlName="no" type="number" /></td>
          <td>
            <input formControlName="type" type="string" />
          </td>
          <td>
            <input formControlName="modifier" type="number" />
          </td>
        </ng-container>
      </tr>
    </table>
  </div>

  <label>Defends As:</label>
  <span>
    <greg-defends-as
      [parentForm]="monsterForm.get('defendsAs')"
    ></greg-defends-as>
  </span>

  <label>Special Qualities:</label>
  <textarea formControlName="specialQualities"></textarea>
</form>
<section>
  <button (click)="addMonsterToSource()">Add</button>
</section>
