import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { TreasureList } from '@treasure/enter-treasure/model/treasure-list.model';
import { EnterTreasureControllerService } from '@treasure/enter-treasure/services/enter-treasure-controller/enter-treasure-controller.service';
import { RollTreasureControllerService } from '@treasure/enter-treasure/services/roll-treasure-controller/roll-treasure-controller.service';
import { Observable } from 'rxjs';

@Component({
  selector: 'greg-treasure-list',
  templateUrl: './treasure-list.component.html',
  styleUrls: ['./treasure-list.component.scss'],
})
export class TreasureListComponent implements OnInit {
  @ViewChild('listImport')
  listImportElementRef: ElementRef;

  treasureList$: Observable<TreasureList>;

  private get listImportInput(): HTMLInputElement {
    return this.listImportElementRef.nativeElement as HTMLInputElement;
  }

  constructor(
    private controllerService: EnterTreasureControllerService,
    private rollTreasureService: RollTreasureControllerService
  ) {}

  ngOnInit(): void {
    this.treasureList$ = this.controllerService.treasureList$;
  }

  clearList(): void {
    this.controllerService.clearList();
  }

  editRecord(index: number): void {
    this.controllerService.editEntryAt(index);
  }

  exportList(): void {
    this.controllerService.exportList();
  }

  importSavedList(): void {
    this.controllerService.importSavedList(this.listImportInput.files[0]);
    this.listImportInput.value = '';
  }

  removeRecord(index: number): void {
    this.controllerService.removeRecordAt(index);
  }

  toggleAreEnteringTreasure(): void {
    this.rollTreasureService.toggleAreEnteringTreasure();
  }
}
<ng-template #nil> Nil </ng-template>

<section>
  <button (click)="exportList()">Export</button>
  <button (click)="clearList()">Clear</button>

  <span>
    <label>Import: </label>
    <input #listImport (change)="importSavedList()" type="file" />
  </span>
</section>

<ng-container *ngIf="treasureList$ | async as treasureList">
  <section>
    <p><strong>Dice to Roll:</strong></p>
    <p>{{ treasureList.diceToRoll | diceRolled }}</p>
    <button (click)="toggleAreEnteringTreasure()">Toggle</button>
  </section>

  <table>
    <tr>
      <th>Type</th>
      <th>Copper</th>
      <th>Silver</th>
      <th>Gold</th>
      <th>Gems</th>
      <th>Jewelry</th>
      <th>Maps or Magic</th>
      <th>&nbsp;</th>
    </tr>
    <tr *ngFor="let entry of treasureList.entries; index as i">
      <td class="left-align">{{ entry.type }}</td>
      <td>{{ entry.copper | speciePipe }}</td>
      <td>{{ entry.silver | speciePipe }}</td>
      <td>{{ entry.gold | speciePipe }}</td>
      <td>
        <ng-container *ngIf="entry.gems.length; else nil">
          <ng-container *ngFor="let gem of entry.gems; let last = last">
            {{ gem | gemOrJewel }}
            <br *ngIf="!last" />
          </ng-container>
        </ng-container>
      </td>
      <td>
        <ng-container *ngIf="entry.jewelry.length; else nil">
          <ng-container *ngFor="let jewel of entry.jewelry; let last = last">
            {{ jewel | gemOrJewel }}
            <br *ngIf="!last" />
          </ng-container>
        </ng-container>
      </td>
      <td>
        <ng-container *ngIf="entry.mapsAndMagic.length; else nil">
          <ng-container *ngFor="let mom of entry.mapsAndMagic; let last = last">
            {{ mom | mapOrMagic }}
            <br *ngIf="!last" />
          </ng-container>
        </ng-container>
      </td>
      <td class="two-across">
        <div class="edit-pencil"><a (click)="editRecord(i)">&#x270F;</a></div>
        <a (click)="removeRecord(i)">X</a>
      </td>
    </tr>
  </table>
</ng-container>
